  `nom` varchar(150) NOT NULL,
  `categorie` varchar(30) NOT NULL,
  `premiere` datetime NOT NULL,
  `derniere` datetime DEFAULT NULL,
  `ville` varchar(100) NOT NULL,
  `salle` varchar(100) NOT NULL,
  `visuel` varchar(150) NOT NULL,
